    public Pelanggan() {
       initComponents();
        konek = koneksi.Koneksidb();
        mulai();
        tampilwaktu();
        updatetable();
    } 
       public void mulai(){
    txthours.setEnabled(false);
    txtdate.setEnabled(false);
    }
    
    public void tampilwaktu(){
    Thread clock=new Thread(){
    public void run(){
     for(;;) {
      Calendar cal = Calendar.getInstance();
      SimpleDateFormat jam = new SimpleDateFormat("hh:mm:ss");
      SimpleDateFormat tanggal = new SimpleDateFormat("dd-MM-yyyy");
      txthours.setText(jam.format(cal.getTime()));
      txtdate.setText(tanggal.format(cal.getTime()));
     }
    }
    };
    clock.start();
    }
     public void updatetable(){
    try{
      String sql = "select * from pelanggan";
      pst = konek.prepareStatement(sql);
      rst = pst.executeQuery();
      tblpelanggan.setModel(DbUtils.resultSetToTableModel(rst));
    }catch(Exception e){
     JOptionPane.showMessageDialog(null, e);
    }
    }
    private void clear(){
    txtid.setText("");
    txtnama.setText("");
    txtalamat.setText("");
    txtnohp.setText("");
    
    }
     
    private void simpan(){
    try{
      String sql = "insert into pelanggan (PelangganID,NamaPelanggan,Alamat,NomorTelepon) value(?,?,?,?)";
     pst = konek.prepareStatement(sql);
        pst.setString(1, txtid.getText());
        pst.setString(2, txtnama.getText());
        pst.setString(3, txtalamat.getText());
        pst.setString(4, txtnohp.getText());
        
        pst.execute();
        JOptionPane.showMessageDialog(null, "data berhasil disimpan");
    }catch(Exception e){
       JOptionPane.showMessageDialog(null, "data gagal disimpan");
    }
    updatetable();
    clear();
    
    }

