    private void btneditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btneditActionPerformed
try{
String id = txtid.getText();
String nama = txtnama.getText();
String harga = txtalamat.getText();
String stok = txtnohp.getText();
String sql = "update pelanggan set NamaPelanggan ='"+nama+"',Alamat='"+harga+"',NomorTelepon='"+stok+"' where PelangganID='"+id+"'";
 pst = konek.prepareStatement(sql);
 pst.execute();
 JOptionPane.showMessageDialog(null, "data berhasil di update");
}catch(Exception e) {
 JOptionPane.showMessageDialog(null, "data gagal diedit");
}
updatetable();
clear();             // TODO add your handling code here:
    }//GEN-LAST:event_btneditActionPerformed

